import React, { useState, useRef } from 'react';
import DrugstoreNavbar from './components/DrugstoreNavbar';
import DrugstoreHero from './components/DrugstoreHero';
import ProductCatalog from './components/ProductCatalog';
import OffersCarousel from './components/OffersCarousel';
import CallToAction from './components/CallToAction';
import DrugstoreFooter from './components/DrugstoreFooter';
import CartButton from './components/CartButton';
import CartPage from './components/CartPage';

const App = () => {
  const [showCart, setShowCart] = useState(false);
  const productsRef = useRef(null);

  const scrollToProducts = () => {
    if (productsRef.current) {
      window.scrollTo({
        top: productsRef.current.offsetTop - 70,
        behavior: 'smooth'
      });
    }
  };

  return (
    <div className="min-h-screen flex flex-col">
      <DrugstoreNavbar scrollToProducts={scrollToProducts} />

      <main className="flex-grow">
        {showCart ? (
          <CartPage onClose={() => setShowCart(false)} />
        ) : (
          <>
            <DrugstoreHero />
            <div ref={productsRef}>
              <ProductCatalog />
            </div>
            <OffersCarousel />
            <CallToAction />
          </>
        )}
      </main>

      <DrugstoreFooter />
      {!showCart && <CartButton onClick={() => setShowCart(true)} />}
    </div>
  );
};

export default App;


